DROP TABLE IF EXISTS `umc_alarm_config`;
CREATE TABLE `umc_alarm_config` (
  `id` int(11) NOT NULL,
  `name` varchar(64) DEFAULT NULL COMMENT '名称',
  `template_id` int(11) DEFAULT NULL COMMENT '模版id',
  `tags` varchar(255) DEFAULT NULL COMMENT '改成存实例id,逗号分隔',
  `alarm_type` varchar(12) DEFAULT NULL COMMENT '提醒方式:email邮箱,sms短信,wechat微信等',
  `alarm_member` varchar(255) DEFAULT NULL COMMENT '提醒对象:张三,李四',
  `alarm_content` varchar(255) DEFAULT NULL COMMENT '告警内容',
  `enable` int(11) DEFAULT NULL,
  `remark` varchar(100) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL,
  `del_flag` char(1) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT '0',
  `create_date` datetime DEFAULT NULL,
  `create_by` varchar(64) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL,
  `update_date` datetime DEFAULT NULL,
  `update_by` varchar(64) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='umc告警配置表';

