  `name` varchar(32) DEFAULT NULL COMMENT '告警名称',
  `tags` varchar(255) DEFAULT NULL COMMENT '对应umc_alarm_config的tags,保存了公用的tags,例如实例instant',
  `gather_time` datetime DEFAULT NULL COMMENT '采集时间',
  `alarm_time` datetime DEFAULT NULL COMMENT '告警时间',
