DROP TABLE IF EXISTS `umc_alarm_rule`;
CREATE TABLE `umc_alarm_rule` (
  `id` int(11) NOT NULL,
  `template_id` int(11) DEFAULT NULL COMMENT '模版id',
  `aggregator` varchar(32) DEFAULT NULL COMMENT '聚合函数:avg,sum,min,max,lastest',
  `operator` int(32) DEFAULT NULL COMMENT '运算符:>=,<=,==',
  `value` double DEFAULT NULL COMMENT '值',
  `trigger_type` int(11) DEFAULT NULL COMMENT '触发类型,1连续?秒,2一旦出现',
  `continuity_time` bigint(20) DEFAULT NULL COMMENT '触发类型为1时,连续的时间,单位秒',
