mapper.select:
select

from ci_trigger
where del_flag!=1


  AND id=#{id,jdbcType=INTEGER}


  AND name LIKE CONCAT('%','${name}','%')


  AND task_id=#{taskId,jdbcType=INTEGER}


  AND enable=#{enable,jdbcType=INTEGER}


  AND DATE_FORMAT(create_date,'%Y-%m-%d %H:%i') >= DATE_FORMAT(#{startDate},'%Y-%m-%d %H:%i')
  AND DATE_FORMAT(create_date,'%Y-%m-%d %H:%i') < DATE_FORMAT(#{endDate},'%Y-%m-%d %H:%i')

select
from ci_trigger where del_flag!=1
AND type = #{type,jdbcType=INTEGER}
