mapper.select:
    SELECT
    ,c.name
    FROM
    erm_cluster_config a
    inner join erm_app_cluster c on a.cluster_id = c.id
    where c.name = #{appName,jdbcType=VARCHAR}

    
      and a.env_type = #{envType,jdbcType=VARCHAR}
    

    
      and a.type = #{type,jdbcType=INTEGER}
    
    limit 1

    SELECT
    ,c.name
    FROM
    erm_cluster_config a
    inner join erm_app_cluster c on a.cluster_id = c.id
    
      
        c.name in
        
          #{appName}
        
      

      
        and a.env_type = #{envType,jdbcType=VARCHAR}
      

      
        and a.type = #{type,jdbcType=INTEGER}
