mapper.select:
SELECT version_id FROM  erm_app_instance
WHERE

	id = #{nodeid}

AND

	env_id = #{envid}

SELECT a.*, h.`hostname` as host, b.`endpoint` as port, b.id as nodeId, c.`name`, c.id as appClusterId, b.env_type as envType FROM scm_version a JOIN erm_app_instance b ON a.id = b.version_id left join erm_host h on h.id=b.host_id JOIN erm_app_cluster c ON b.cluster_id = c.id
a.del_flag = #{DEL_FLAG_NORMAL} AND c.del_flag = #{DEL_FLAG_NORMAL}
AND c.id = #{appClusterId}
AND b.id = #{instanceId}
AND b.env_type = #{envType}

mapper.update:
update scm_version_detail

	
		version_id = #{versionId},
	
	
		`type` = #{type},
	
	
		content = #{content},
	
	
		remark = #{remark}
	

where id = #{id}

mapper.select:
SELECT rh.id AS "releaseId", i.version_id AS "version", vd.namespace_id AS "namespace_id", vd.namespace_id AS "namespace", vd.type AS "type", vd.content AS "content", rh.create_date AS "createDate" FROM scm_release_detail rd INNER JOIN scm_release_history rh ON rh.id = rd.release_id INNER JOIN erm_app_instance i ON i.id = rd.instance_id INNER JOIN erm_app_cluster g ON g.id = i.cluster_id INNER JOIN scm_version_detail vd ON vd.version_id = i.version_id left join erm_host h on h.id=i.host_id
AND g.`name` = #{cluster}
AND vd.namespace_id IN #{item}
AND rh.id = #{meta.releaseId} AND rh.version_id = #{meta.version}
AND h.`hostname` = #{instance.host} AND g.`endpoint` = #{instance.endpoint}
ORDER BY rh.create_date DESC LIMIT 1

mapper.update:
UPDATE scm_release_detail rd
INNER JOIN erm_app_instance i ON i.id = rd.instance_id

	rd.result = #{detailsJSONString},
	rd.status = #{status.value},
	rd.description = #{description}


	
	
		AND i.`host` = #{instance.host}
		AND i.`endpoint` = #{instance.endpoint}
	
	
		AND i.version_id = #{meta.version}
	
	
		AND rd.release_id = #{meta.releaseId}
