mapper.select:
select * from umc_alarm_config c
left join umc_alarm_template t on t.id=c.template_id
where c.template_id=#{templateId,jdbcType=INTEGER} and c.cluster_id=#{appClusterId,jdbcType=INTEGER}

select ,t.name template_name,cg.name contact_group_name from umc_alarm_config c left join umc_alarm_template t on t.id=c.template_id left join umc_alarm_contact_group cg on c.contact_group_id=cg.id where c.del_flag!=1
and c.template_id=#{templateId,jdbcType=INTEGER}
and c.contact_group_id=#{contactGroupId,jdbcType=INTEGER}
