mapper.select:
select
,p.pipe_name,c.name cluster_name,u.user_name create_by_name, p.environment from ci_pipe_history h left join ci_pipeline p on p.id=h.pipe_id left join erm_app_cluster c on p.cluster_id=c.id left join sys_user u on u.id = h.create_by where h.del_flag!=1
AND p.pipe_name LIKE CONCAT('%','${pipeName}','%')
AND p.environment = #{environment,jdbcType=VARCHAR}
AND h.provider_kind = #{providerKind,jdbcType=VARCHAR}
AND c.name LIKE CONCAT('%','${clusterName}','%')
AND h.orchestration_type = #{orchestrationType,jdbcType=INTEGER}
AND h.orchestration_type is null
AND h.orchestration_id = #{orchestrationId,jdbcType=BIGINT}
AND DATE_FORMAT(h.create_date,'%Y-%m-%d %H:%i') >= DATE_FORMAT(#{startDate},'%Y-%m-%d %H:%i') AND DATE_FORMAT(h.create_date,'%Y-%m-%d %H:%i') <= DATE_FORMAT(#{endDate},'%Y-%m-%d %H:%i')
and h.organization_code in
#{organizationCode}
order by h.create_date desc

mapper.update:
update ci_pipe_history set status=4
where status in(0,1)
and TIMESTAMPDIFF(second ,create_date,now())>#{time,jdbcType=BIGINT};
