mapper.select:
SELECT
	COUNT(1)
FROM dts_gen_table

	project_id = #{projectId,jdbcType=INTEGER}
	AND table_name = #{tableName,jdbcType=VARCHAR}
	AND del_flag != 1

mapper.update:
UPDATE dts_gen_table
project_id = #{projectId,jdbcType=INTEGER}, table_name = #{tableName,jdbcType=VARCHAR}, entity_name = #{entityName,jdbcType=VARCHAR}, comments = #{comments,jdbcType=VARCHAR}, module_name = #{moduleName,jdbcType=VARCHAR}, sub_module_name = #{subModuleName,jdbcType=VARCHAR}, function_name = #{functionName,jdbcType=VARCHAR}, function_name_simple = #{functionNameSimple,jdbcType=VARCHAR}, function_author = #{functionAuthor,jdbcType=VARCHAR}, options = #{options,jdbcType=VARCHAR}, status = #{status,jdbcType=VARCHAR}, remark = #{remark,jdbcType=VARCHAR}, del_flag = #{delFlag,jdbcType=INTEGER}, create_date = #{createDate,jdbcType=TIMESTAMP}, create_by = #{createBy,jdbcType=VARCHAR}, update_date = #{updateDate,jdbcType=TIMESTAMP}, update_by = #{updateBy,jdbcType=VARCHAR}
id = #{id,jdbcType=INTEGER}
