mapper.select:
select
,
from cmdb_app_cluster c left join cmdb_ssh s on s.id = c.ssh_id where c.id = #{id,jdbcType=BIGINT}

select
from cmdb_app_cluster where del_flag!=1
AND name LIKE CONCAT('%','${clusterName}','%')
AND deploy_type = #{deployType,jdbcType=INTEGER}
and organization_code in
#{organizationCode}
