mapper.select:
SELECT
COUNT(1)
FROM udc_gen_table

	project_id = #{projectId,jdbcType=BIGINT}
	AND table_name = #{tableName,jdbcType=VARCHAR}
	AND del_flag != 1

mapper.update:
UPDATE udc_gen_table
project_id = #{projectId,jdbcType=BIGINT}, table_name = #{tableName,jdbcType=VARCHAR}, entity_name = #{entityName,jdbcType=VARCHAR}, comments = #{comments,jdbcType=VARCHAR}, module_name = #{moduleName,jdbcType=VARCHAR}, sub_module_name = #{subModuleName,jdbcType=VARCHAR}, function_name = #{functionName,jdbcType=VARCHAR}, function_name_simple = #{functionNameSimple,jdbcType=VARCHAR}, function_author = #{functionAuthor,jdbcType=VARCHAR}, extra_options_json = #{extraOptionsJson,jdbcType=VARCHAR}, status = #{status,jdbcType=VARCHAR}, remark = #{remark,jdbcType=VARCHAR}, del_flag = #{delFlag,jdbcType=INTEGER}, create_date = #{createDate,jdbcType=TIMESTAMP}, create_by = #{createBy,jdbcType=BIGINT}, update_date = #{updateDate,jdbcType=TIMESTAMP}, update_by = #{updateBy,jdbcType=BIGINT}
id = #{id,jdbcType=BIGINT}
